import React, {useState} from 'react';
import SOne from '../elements/slider/SlideOne';
import styled from 'styled-components';
import {absolute} from '../utilities';

const SliderBase = ({className}) => {

  const [x, setX] = useState(0);

  let sliderComponents = [<SOne/>,2,3,4]; 

  const goLeft = () => {
    setX(x + 100);
    if(x === 0 ? setX( -100 * (sliderComponents.length -1 ) ) : setX(x + 100) );
  }

  const goRight = () => {    
    if(x === -100 * (sliderComponents.length -1 ) ? setX(0) : setX(x - 100) );
  }

  return (

    <div className={className}>
      {sliderComponents.map( (component,idx) => {
            return (
              <div key={idx} className="slide" style={{ transform:`translateX(${x}%)` }}>
                {component}
              </div>
            )
      })}

      <button className="slide_btn_left" onClick={goLeft}>left</button>
      <button className="slide_btn_right" onClick={goRight}>right</button>
    </div>
  );
}

const Slider = styled(SliderBase)`
  background-color: red;
  width: 100%;
  height: 80vh;
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;  

  .slide{ 
    border: solid 3px blue;
    min-width: 100%;
    height: 90%;
    position: relative;
    transition: .5s;
  }

  .slide_btn_left{
    background-color: transparent;
    ${absolute({ y:"50%" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
    cursor: pointer;
  }

  .slide_btn_right{
    background-color: transparent;
    ${absolute({ y:"50%", xP:"right", x:".5rem" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
    cursor: pointer;
  }

`;


export default Slider;
