import React from 'react';
import styled from 'styled-components';
import {setFlex, fixed, colors, demotion, elevation, absolute} from '../utilities';
import { NavLink } from 'react-router-dom';
import Rent from '../elements/svg/Rent';
import Security from '../elements/svg/Security';
import Payment from '../elements/svg/Payment';
import Cancellations from '../elements/svg/Cancellations';

const ConditionsMenuBase = ({ className }) => {

    return (

        <div className={className}>
            <ul className="nav_list">
                <li className="nav_condition">
                    <NavLink 
                        className="nav_link" 
                        activeClassName="active_link"             
                        exact to={`/condiciones`}>          
                        <Rent className="icon" size="35"/>
                        <p className="text">Renta</p>
                    </NavLink>
                </li>
                <li className="nav_condition">
                    <NavLink
                        className="nav_link" 
                        activeClassName="active_link"             
                        exact to={`/condiciones/seguridad`}>        
                        <Security className="icon" size="35"/>
                        <p className="text">Seguridad</p>
                    </NavLink>
                </li>
                <li className="nav_condition">
                    <NavLink
                        className="nav_link" 
                        activeClassName="active_link"             
                        exact to={`/condiciones/pago`}>        
                        <Payment className="icon" size="35"/>
                        <p className="text">Pago</p>
                    </NavLink>
                </li>
                <li className="nav_condition">
                    <NavLink
                        className="nav_link" 
                        activeClassName="active_link"             
                        exact to={`/condiciones/cancelaciones`}>        
                        <Cancellations className="icon" size="25"/>
                        <p className="text">Cancelaciones</p>
                    </NavLink>
                </li>
            </ul>
        </div>
    )
}

const Condiciones = styled(ConditionsMenuBase)`
    width: 18vw;
    margin-top: 6rem;
    position: relative;

    li{
        margin-top: 2rem;
    }

    .icon{
        display: inline-block;
    }

    .text{
        display: inline-block;
        margin-left: 1rem;
        font-size: 1.1rem;
        letter-spacing: .1rem;
        font-weight: 700;
        padding-bottom: 0.7rem;
    }


    .nav_list{
        position: relative;
        list-style: none;
        // margin-left: 2rem;
        // padding: 3rem 0;
        // display: block;   
        width: 100%;

        .nav_condition{
          position: relative;        
          height: 5.5rem;        
          display: block;        
          border-top-left-radius: .5rem;
          border-top-right-radius: .5rem;
          ${elevation[5]};
          border-bottom-left-radius: .5rem;
          border-bottom-right-radius: .5rem;
          ${setFlex({ x:"flex-start"})};

          &:hover{
              background-color: ${colors.white_trans};
              ${elevation[2]};
          }

          .nav_link{
            width: 100%;
            // padding: 1rem;
            margin-top: 1.5rem;
            margin-left: 1rem;
            color: ${colors.white_1};
            text-decoration: none;
            text-transform: capitalize;
            font-size: 1rem;
            font-weight: bold;
            cursor: pointer;
          }
        }
      }

      .active_link{
        position: relative;        
        height: 5.5rem;        
        display: block;
        cursor: pointer;
        border-top-left-radius: .5rem;
        border-top-right-radius: .5rem;
        border-bottom-left-radius: .5rem;
        border-bottom-right-radius: .5rem;
        background-color: ${colors.white_trans};
        ${elevation[0]};
      }

`;

export default Condiciones;
