import React, {useEffect} from 'react';
//import useCustomFetch from '../../hooks/useCustomFetch';
import {useSelector, useDispatch} from 'react-redux';
import ItemListCard from '../../layout/ItemListCard';
import {ItemListContainer} from '../../elements/Container';
import {listItems} from '../../actions/itemActions';

const ItemList = ({match }) => {

    /*
        const [items, setItems] = useState([]);
    const [url, setUrl] = useState(null);
    const [results, err, loading] = useCustomFetch(url);

    const category = match.params.category;

    useEffect( () => {
        setUrl(`/items/${category}`)
    }, [url, category]);

    useEffect( () => {
        setItems(results)
    }, [results]);
    */

    const itemList = useSelector( state => state.items );
    const {items, loading, error} = itemList;
    const dispatch = useDispatch();

    useEffect( () => {
        dispatch(listItems(match.params.category))
    },[dispatch, match.params.category]);

    return (
        <>          
          <ItemListContainer>
            <h3>{category}</h3>
            {items && items.map( (item,idx) =>              
                <ItemListCard                     
                    key={idx}
                    id={item.id}
                    nombre={item.nombre}
                    tamaño={item.tamaño}
                    precio={item.precio}                    
                    paquete={item.paquete}
                    category={match.params.category}
                    fotoCover={item.fotoCover}
                />
            )}            
          </ItemListContainer>
        </>
    );
};

export default ItemList;
