import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, colors, absolute, below} from '../../utilities';
import {SliderContainer} from '../Container';
import Micro from '../svg/Micro.jsx';
import {SliderLink} from '../Button';


const Rockolas = ({className}) => {
  return (
<SliderContainer className={className}>        
      <div className="icon"><Micro size={40}/></div>      
      <h3 className="title">Rockolas</h3>

      <div className="img_box">
        <img className="img" src="https://res.cloudinary.com/noryan/image/upload/v1596843329/foto_covers/rockola_slider.png" alt=""/>
      </div>      
      <div className="text_box">
        <p className="txt">Pantalla touch, iluminación  más de 150 mil canciones y videos</p>
      </div>
      <div className="link">
        <SliderLink>
          <Link to={'/items/rockola'}>Explorar</Link> 
        </SliderLink>
      </div>
</SliderContainer>
  );
}

const SlideRock = styled(Rockolas)`
  background-image: linear-gradient(to right, ${colors.blue_3}, ${colors.noryan_blue} );
  position: relative;

  .title{
    width: auto;
    margin: 1rem auto;
    padding-top: 1.5rem;
    padding-left: 13%;    
  }

  .img_box{
    height: 70%;
    width: 20%;
    ${setFlex({x:"flex-start"})};    
    margin-left: 2rem;

    img{
      height: 100%;
      margin-left: 3rem;
    }
  }

  .text_box{
    width: 60%;
    ${absolute({ x:"30%", y:"30%" })};

    .txt{
      margin-bottom: 5rem;
    }
  }

  .link{
    ${absolute({ x:"50%", y:"60%" })};
  }

  ${below.md`
    .title{            
      padding-left: 20%;      
    }

    .img_box{
      height: 70%;
      width: 20%;
      ${setFlex({x:"flex-start"})};    
      margin-left: 1.5rem;

      img{
        height: 100%;
        margin-left: 1.5rem;
      }
    }

    .text_box{
      width: 50%;
      ${absolute({ x:"40%", y:"30%" })};    

      .txt{
        margin-bottom: 5rem;
        font-size: 1.8rem;
        font-weight: bold;
        text-shadow: 6px 6px 12px rgba(0,0,0,0.4),
              -6px -6px 12px rgb(210, 233, 249, .5);
      }
    }

  `}

${below.sm`
  .title{            
    font-size: 1.7rem;
    padding-left: 25%; 
  }

  .img_box{
    height: 60%;
    width: 20%;
    ${setFlex({x:"flex-start"})};    
    margin-left: .5rem;

    img{
      height: 100%;
      margin-left: .5rem;
    }
  }

  .text_box{
    width: 70%;
    ${absolute({ x:"30%", y:"30%" })};    

    .txt{      
      margin-bottom: 5rem;
    }
  }

  .link{
    position: static;    
  }
`}

${below.xsm`
  .title{           
    font-size: 1.4rem;
    padding-left: 30%; 
  }

  .img_box{
    height: 50%;
    width: 15%;
    ${setFlex({x:"flex-start"})};    
    margin-left: .5rem;

    img{
      height: 100%;
      margin-left: .5rem;
    }
  }

`}
`;

export default SlideRock;
