import React from 'react'
import {Link} from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex} from '../utilities';

function Card({className, nombre, desc, observaciones, precio, medidas, fotoCover, fotos, back}) {
    return (                		    
      <div className={className} >
        <h2 onClick={back}>{nombre}</h2>

        {/* ---  PICTURES --- */}
        <div className="product-pics">
          <div className="foto_cover">
            <img src={fotoCover} alt={nombre} className="card__img"/>
          </div>
          <div className="fotos">
          {fotos && fotos.map( (foto, idx) => 
            <img  key={idx}  src={foto}  alt="" className="card__img"/>
          )}
          </div>
        </div>

        {/* ---  DESCRIPTION --- */}

        <div className="dtl__box">            
          <div className="description">
            <h4>Descripci√≥n</h4>
            <div className="desc_txt">{desc}</div>
          </div>
          <div className="medidas">
            <h4>Medidas</h4>
            <ul>
              {medidas && medidas.map( (medida, idx) => {
                return <li key={idx}>{medida}</li>;
              })}
            </ul>
          </div>              
          <div className="precio"><p>$ {precio}</p></div>
          <p className="observaciones">{observaciones}</p>          
        </div>    
        <Link to='/contacto' className="round-black-btn">Reservar</Link>
      </div>        


    )
}

const ItemDetailCard = styled(Card)`
  width: 100%;
  margin: 1rem auto;
  padding: 1.5rem;
  color: ${colors.white_1};
  background-color: blue;
  display: flex;
  flex-wrap: wrap;

  h2{
    width: 90%;    
    text-align:center;
    text-transform: uppercase;
    font-size: 1.2rem;    
  }

  .product-pics{
    background-color: orange;   
    width: 50%;        

    img{
      height: 4rem;
    }
  }

`;

export default ItemDetailCard

