import {ITEM_LIST_REUQEST, ITEM_LIST_SUCESS, ITEM_LIST_FAIL, ITEM_DETAIL_REQUEST, ITEM_DETAIL_SUCCESS, ITEM_DETAIL_FAIL} from './types';
import service from '../config/axios_service';

const listItems =  (category) => async (dispatch) => {
  try {
    dispatch({ type: ITEM_LIST_REUQEST });
    const response = await service.get(`/items/${category}`);
    console.log(response)
    dispatch({ type: ITEM_LIST_SUCESS, payload: response.data.data.items });
  } catch (error) {
    dispatch({type: ITEM_LIST_FAIL, payload: error.message});
  }
}

const itemDetail =  (category, id) => async (dispatch) => {
  try {
    dispatch({ type: ITEM_DETAIL_REQUEST });
    const response = await service.get(`/items/${category}/${id}`);
    console.log(response)
    dispatch({ type: ITEM_DETAIL_SUCCESS, payload: response.data.data.item });
  } catch (error) {
    dispatch({type: ITEM_DETAIL_FAIL, payload: error.message});
  }
}


export {listItems, itemDetail};

