import React from 'react';
import styled from 'styled-components';
import {setFlex, colors} from '../../utilities';

const SalesBase = ({className}) => {
    return (
        <div className={className}>
        <h3>Venta de juegos infantiles</h3>
        <div className="box_container">
            <div className="box">
                <div className="icon"></div>
                <p className="txt_box">Variedad</p>
            </div>
            <div className="box">
                <div className="icon"></div>
                <p className="txt_box">Calidad</p>
            </div>
            <div className="box">
                <div className="icon"></div>
                <p className="txt_box">Confianza</p>
            </div>
            <div className="box">
                <div className="icon"></div>
                <p className="txt_box">Forma de pago y facturaci√≥n</p>
            </div>
        </div>
        </div>
    );
};

const Venta = styled(SalesBase)`
    background-color: red;
    ${setFlex()};
    width: 95%;
    margin: 2rem auto;

    h3{
      color: ${colors.white_1};
      font-size: 2.5rem;
      text-align: center;      
    }

    .box_container{
      background-color: blue;
      height: 3rem;
      width: 95%;

        .box{
            background-color: yellow;
            height: 3rem;
            width: 95%;

            .icon{
              background-color: green;
              height: 3rem;
              width: 95%;
            }

            .txt_box{
              background-color: orange;
              height: 3rem;
              width: 95%;
            }
        }
    }
`;


export default Venta;
