import React, {useState} from 'react';
import SlideNoryan from '../elements/slider/SlideNoryan';
import SlideInflables from '../elements/slider/SlideInflables';
import SlideFut from '../elements/slider/SlideFut';
import SlideRock from '../elements/slider/SlideRock';
import SlideMaquinitas from '../elements/slider/SlideMaquinitas';
import SlideJumpings from '../elements/slider/SlideJumpings';
import styled from 'styled-components';
import Next from '../elements/svg/Next.jsx';
import Prev from '../elements/svg/Prev.jsx';
import {absolute, colors, below} from '../utilities';

const SliderBase = ({className}) => {

  const [x, setX] = useState(0);

  let sliderComponents = [ <SlideNoryan/>,<SlideInflables/>, <SlideFut/>,<SlideRock/>,<SlideMaquinitas/>,<SlideJumpings/>]; 

  const goLeft = () => {
    setX(x + 100);
    if(x === 0 ? setX( -100 * (sliderComponents.length -1 ) ) : setX(x + 100) );
  }

  const goRight = () => {    
    if(x === -100 * (sliderComponents.length -1 ) ? setX(0) : setX(x - 100) );
  }



  return (

    <div className={className}>
      {sliderComponents.map( (component,idx) => {
            return (
              <div key={idx} className="slide" style={{ transform:`translateX(${x}%)` }}>
                {component}
              </div>
            )
      })}

      <button className="slide_btn_left" onClick={goLeft}><Prev/></button>
      <button className="slide_btn_right" onClick={goRight}><Next/></button>
    </div>
  );
}

const Slider = styled(SliderBase)`  
  width: 100%;  
  box-sizing: border-box;
  margin-top: 2rem;
  padding: 0;
  display: flex;
  align-items: center;  

  .slide{ 
    min-width: 100%;
    height: 100%;
    position: relative;
    transition: .5s;
    border-radius: .8rem;  
  }

  .slide_btn_left{
    background-color: transparent;
    ${absolute({ y:"50%" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
    cursor: pointer;
    border: none;
    outline: 0;
    border-radius: .8rem;

    &:hover{
      background-color: ${colors.blue_1};
      opacity: .3;
    }

    &:active{
      border: none;
    }

  }

  .slide_btn_right{
    background-color: transparent;
    ${absolute({ y:"50%", xP:"right", x:".5rem" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
    cursor: pointer;
    border: none;
    outline: 0;
    border-radius: .8rem;

    &:active{
      border: none;
    }
  }

  ${below.sm`    
    .slide_btn_left{      
      width: 25%;
      height: 50%;    
    }

    .slide_btn_right{                  
      width: 25%;
      height: 50%;    
    }
`}

`;


export default Slider;
