import React, {useState, useEffect} from 'react';
import { Route } from 'react-router-dom';
import {RentContainer} from '../../elements/Container';
import Menu from '../../layout/CategoryMenu';
import ItemList from '../items/ItemList';
import ItemDetail from '../items/ItemDetail';
//import SearchBar from '../../layout/SearchBar'; component={ItemList} component={ItemDetail}


const Renta = ({className, salePrice}) => {    

    const [price, setPrice] = useState('');

    useEffect(() => {
        salePrice ? setPrice('sale') : setPrice('rent');
    }, []);

    //console.log(price)

    return (
        <>
        <RentContainer className={className}>
            <Menu/>            
            <div className="item_list">
              <Route 
                exact path={`/items/:category`}                 
                render={(props) => (
                    <ItemList {...props} price={price} />
                  )}
                />
              <Route 
                exact path={`/items/:category/:id`}                
                render={(props) => (
                    <ItemDetail {...props} price={price} />
                  )}
                />
            </div>
        </RentContainer> 
        </>
    );
};

export default Renta;
