import React from 'react'
import {Link} from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex} from '../utilities';

function Card({className, nombre, desc, observaciones, precio, medidas}) {

  console.log(medidas);

    return (
        <div className={className}>            		    
          <div className="product-dtl">
            <h2>{nombre}</h2>
              <p className="observaciones">{observaciones}</p>
              <div className="dtl__box">                                                 
                <div className="medidas">
                  <h4>Medidas</h4>
                  <ul>
                    {medidas.map((medida, idx) => (
                      <li
                        key={idx}
                        >{medida}</li>
                    )) }
                  </ul>
                </div>
                <div className="precio">
                  <p>$ {precio}</p>
                </div>
              </div>                   
              <div className="description">
                <h4>Descripci√≥n</h4>
                <div className="desc_txt">
                  {desc}
                </div>                
              </div>              
			</div>

	        <div className="product-pics">
              <Link to='/contacto' className="round-black-btn">Reservar</Link>
		    </div>

        </div>
    )
}

const ItemDetailCard = styled(Card)`
    color: ${colors.white_1};
    ${setFlex({ x:"space-between", y:"felx-start" })};
    font-size: .75rem;

    .product-dtl {
	  width: 100%;
      margin: 1rem;
      margin-left: 2rem;

      h2{
        text-align:center;
        text-transform: uppercase;
        font-size: 1.2rem;        
      }

      .observaciones{        
        line-height: 1.1rem;
      }

      .dtl__box{
        ${setFlex({ x:"space-between" })};

        .precio{
          width: 50%;
          ${setFlex({ x:"flex-end", y:"flex-end" })};
          margin-right: 3rem;

          p {
            font-size: 1rem;
            font-weight: bolder;
            letter-spacing: .1rem;
          }   
        }        
      }
      .description{        
          .desc_txt{
            line-height: 1.1rem;
            text-align: justify;
          }
        }  

    }

    .product-pics{
      background-color: ${colors.white_trans};
      width: 100%;
      height: 50vh;
      margin-top: 2rem;
      margin-right: 2rem;
    }

`;

export default ItemDetailCard
