import React, {useState, useEffect} from 'react';
import useCustomFetch from '../../hooks/useCustomFetch';
import {ItemDetailContainer} from '../../elements/Container';
import ItemDetailCard from '../../layout/ItemDetailCard';

function ItemDetail({match}) {

    const [item, setItem] = useState({});
    const [url, setUrl] = useState(null);
    const [result, loading] = useCustomFetch(url);

    useEffect( () => {
        setUrl(`/items/${match.params.category}/${match.params.id}`)
    }, [url, match.params.category, match.params.id]);

    useEffect( () => {
        setItem(result)
    }, [result]);

    return (
        <ItemDetailContainer>
            {loading && url  && <div>LOADING</div>}
            <ItemDetailCard
                nombre={item.nombre}
                precio={item.precio}
                desc={item.descripcion}
                observaciones={item.observaciones}
                medidas={item.medidas}
            />            
        </ItemDetailContainer>
    )
}

export default ItemDetail;
