import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, absolute, colors} from '../../utilities';
import {SliderContainer} from '../Container';
import Castle from '../svg/Castle.jsx';
import {SliderLink} from '../Button';




const Inflables = ({className}) => {
  return (
    <SliderContainer className={className}>    
      <div className="icon"><Castle size={40}/></div>
      <h3 className="title slide_title">Juegos Inflables</h3>

        <img className="img" src="https://partyplace.mx/wp-content/uploads/2019/06/2-2.png" alt=""/>

      <div className="text">
        <p className="txt">MÃ¡s de 60 modelos diferentes</p>
        <p className="txt">desde $600 por evento</p>
      </div>      
      <SliderLink w="25rem">
        <Link to={'/items/inflable'}>Ver modelos ...</Link> 
      </SliderLink>
    </SliderContainer>
  );
}

const SlideInflables = styled(Inflables)`  
  background-image: linear-gradient(to right, ${colors.noryan_blue}, ${colors.blue_3});
  position: relative;
  ${setFlex()};


  img{
   margin: 4rem auto;
  }


`;

export default SlideInflables;
