import React from "react";
import styled from 'styled-components';
import {setFlex, elevation, absolute, colors } from '../utilities';
import Search  from '../elements/svg/Search.jsx';

function SearchBarBase({ className, searchHandler, sizeHandler, priceHandler}) {

    return (
        <div className={className}>
            <div className="filter_box">                                                                 
              <div className="search_box">
                <input                
                    type="text"
                    placeholder="Busca un producto..."
                    name="searchKeyword"
                    onChange={searchHandler}
                />
                <div className="search"><Search/></div>
              </div>                    
              <select name="size" onChange={sizeHandler}>
                  <option value="">Tamaño</option>
                  <option value="chico">Chico</option>
                  <option value="mediano">Mediano</option>
                  <option value="grande">Grande</option>
              </select>
              <select name="price" onChange={priceHandler}>
                  <option value="">Precio</option>
                  <option value="-precio">Mayor</option>
                  <option value="precio">Menor</option>
                  <option value="precio[lte]=1500"> $500 - $1,500 </option>
                  <option value="precio[gte]=1500"> mas de $1,500</option>
                  <option value="precio[gte]=2000"> mas de $2,000</option>
                  <option value="precio[gte]=3000"> mas de $3,000</option>
                  <option value="precio[gte]=5000"> mas de $5,000</option>
              </select>           
            </div>
        </div>
    );
}

const SearchBar = styled(SearchBarBase)`
    position: relative;
    width: 100%;
    height: 2rem;
    margin: 0rem auto;
    margin-bottom: 1rem;
    ${elevation[3]};  
    border-radius: .5rem;

    .filter_box{      
      ${setFlex({x:"space-between", y:"flex-start"})};
      flex-flow: row nowrap;
      border-radius: .8rem;
      padding:0rem 1rem;      

      .search_box{
        position: relative;

        input{        
          margin: .5rem auto;
          border: none;
          outline: none;
          border-radius: .8rem;        
          ${elevation[1]};
          padding-left: .5rem;

          &::placeholder {
            opacity: .5;
            font-size: .7rem;
            padding-left: .5rem; 
          }
        }

        .search{
          background-color: red;
          ${absolute({x:"10%", y:"25%"})};
        }    
      }



      select{
        width: 20%;
        height: 1.1rem;
        margin: .5rem auto;
        border: none;
        outline: none;
        border-radius: .8rem;        
        ${elevation[1]};
        font-size: .7rem;
        ${colors.txt_3};
      }
    }
`;
export default SearchBar;

