import styled from 'styled-components';
import { colors, setFlex, fixed, demotion, below, above } from '../utilities';

export const Wrapper = styled.div`    
    height: 100vh;
    width: 100%;
    background-image: linear-gradient(${colors.blue_1}, ${colors.blue_5});    
    overflow: auto;
`;

export const RentContainer = styled.div`
    position: relative;
    width: 100%;    
    ${setFlex({ x:"space-around", y:'flex-start' })};    

    .item_list{ /*  */
        position: relative;
        width: 85%;    
        margin: 1rem 1rem;
        margin-right: 1rem;
        overflow: auto;  
    }

    ${below.md`      
      justify-content: center;
      flex-direction: column;
      background-color: blue;

      .item_list{
        position: relative;
        width: 95%;    
        margin: -.5rem auto;        
        overflow: auto;
        background-color: gray;  
      }

    `};

`;

export const ItemListContainer = styled.div`
    width: 100%;
    height: 65vh;    
    flex-direction: row;
    flex-wrap: wrap;
    ${demotion[2]};
    padding-top: 2rem;
    padding-left: 1rem;
    border-radius: 1.5rem;
    ${setFlex({ x:"flex-start", y:"flex-start" })};
    overflow: auto;
    background-color: black;

    ${above.lg`height: 75vh;`};

    @media all and (max-width: 1080px) and (min-width: 768px) and (orientation: portrait){          
      height: 75vh;

    }

    ${below.sm`        
        width: 100%;

    `};

`;


export const ItemDetailContainer = styled.div`
    position: relative;
    width: 100%;
    height: 70vh;        
    border-radius: 1.5rem;
    overflow: auto;    
    ${demotion[2]};
`;

export const PaqContainer = styled.div`
    position: fixed;
    width: 100%;
    margin: 9.1rem 1rem 0rem 1rem;
    ${setFlex({ x:"space-around", y:'flex-start' })};    


    .paquetes_list{
        position: relative;
        width: 95%;    
        margin: 0rem 1rem;
        overflow: auto;         
    }
`;

export const PaqListContainer = styled.div`
    width: 100%;
    height: 70vh;    
    flex-direction: row;
    flex-wrap: wrap;
    ${demotion[2]};
    padding-top: 2rem;
    padding-left: 1rem;
    border-radius: 1.5rem;
    ${setFlex({ x:"flex-start", y:"flex-start" })};
    overflow: auto;
`;

export const SectionsContainer = styled.div`
    position: relative;
    margin-top: 9rem;
    ${fixed( )};
    ${setFlex( )};
`;

export const Space = styled.div`
    height: ${props => props.h || '3rem' };
    width: ${props => props.w || '100%' };    
`;
