import React, {useState} from 'react';
import AliceCarousel from 'react-alice-carousel';
import "react-alice-carousel/lib/alice-carousel.css";

export default function Carousel() {

  const [ currentIndex, setCurrentIndex ] = useState(0);
  const [responsive, setResponsive] =  useState({ 1024: { items: 3 } });

  const items = ["https://res.cloudinary.com/noryan/image/upload/v1595728733/foto_covers/anexo.png", "https://res.cloudinary.com/noryan/image/upload/v1595728733/foto_covers/anexo.png", "https://res.cloudinary.com/noryan/image/upload/v1595728733/foto_covers/anexo.png", "https://res.cloudinary.com/noryan/image/upload/v1595728733/foto_covers/anexo.png", "https://res.cloudinary.com/noryan/image/upload/v1595728733/foto_covers/anexo.png"];

  const slideTo = (i) => setCurrentIndex(i);
  const slideNext = () => setCurrentIndex(currentIndex + 1);
  const slidePrev = () => setCurrentIndex(currentIndex - 1);


  const onSlideChanged = (e) => setCurrentIndex(e.item);

  const thumbItem = (item, i) => <span onClick={() => slideTo(i)}>* </span>

  const galleryItems = () => {
    return items.map((i) => <h2 key={i}> {i}</h2>)
  }

  return (
    <div>
       <AliceCarousel
          dotsDisabled={true}
          buttonsDisabled={true}
          items={items}
          responsive={responsive}
          slideToIndex={currentIndex}
          onSlideChanged={onSlideChanged}
        />

        <ul>{items.map(thumbItem)}</ul>
        <button onClick={slidePrev}>Prev button</button>
        <button onClick={slideNext}>Next button</button>
    </div>    
  )
}
