import React from 'react';
import styled from 'styled-components';
import {colors, setFlex} from '../utilities';

const Load = ({className}) => {

  return (
    <div className={className}>      
      <figure>		
        <img src="https://kmtonline.com.au/resources/front/landing/images/loader.gif" alt="loader"/>
      </figure>
      <p className="txt">Cargando ...</p>      
    </div>
  );
};

const Loader = styled(Load)`
	${setFlex({y:"flex-start"})};
  flex-flow: column nowrap;

  figure{
   height: 40vh; 
  }

  p {
    width: 100%;
    color: ${colors.white_1};
    margin: 0rem auto;        
    margin-top: 4rem;
    font-size: 2rem;
    text-align: center;
  }



`;

export default Loader;
