import React from 'react';
import styled from 'styled-components';
import {setFlex, fixed, colors, below} from '../utilities';
import { Whats, Phone } from '../elements/Icons';
import { TopBarButton } from '../elements/Button';
import { NavLink, Link } from 'react-router-dom';


function TopBarBase({ className }) {
    return (
      <div className={className}>
        <Link className="link" exact to='/'><div className="logo_top"></div></Link>
        <div className="btn_wrapper">
         <a target="blank" href="https://api.whatsapp.com/send?phone=525536150824&amp;text=Hola,%20me%20contacto%20por%20la%20renta%20de%20juegos." className="contact_numbers">
           <Whats size={25}/>55-3615-0824</a>
         <a target="blank" href="tel:54457537" className="contact_numbers"><Phone size="25"/>5445 - 7537</a>
         <TopBarButton>
          <span>Iniciar sesi√≥n</span>
         </TopBarButton>
        </div>
      </div>
    )
}

const TopBar = styled(TopBarBase)`    
    ${fixed()}
    width: 100%;
    height: 3rem;
    padding: 0;
    background-color: ${colors.blue_2};           
    ${setFlex({ x:"space-between"})};

    .link{
      width: 80%;
    }

    .logo_top{      
      position: relative;
      background-image: url('https://i.postimg.cc/vHM2Xfwk/footer-logo.png');
      background-position: left;
      background-repeat: no-repeat;
      width: 15%;
      height: 3rem;
      margin-left: 1rem;
      cursor: pointer;
    }

    .btn_wrapper{
      width: 45%;
      ${setFlex({ x:"space-around" })};
    }

    .contact_numbers{
      color: ${colors.white_1};
      text-decoration: none;
      font-size: 1rem;
    }

`;


export default TopBar;
