import React, {useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {salesList} from '../../actions/itemActions';
import ItemListCard from '../../layout/ItemListCard';
import {ItemListContainer} from '../../elements/Container';

const Catalogo = ({match}) => {

    const itemList = useSelector( state => state.saleItems);
    const {saleItems, loading, error} = itemList;
    const dispatch = useDispatch();

    useEffect( () => {
       dispatch(salesList()); 
    },[dispatch]);

    console.log(saleItems)

    return loading ? <div>Loading...</div> :
    error ? <div>{error}</div> :
    <ItemListContainer>
    <h3>{match.params.category}</h3>
    {saleItems && saleItems.map( (item,idx) =>              
        <ItemListCard                     
            key={idx}
            id={item.id}
            nombre={item.nombre}
            tamaño={item.tamaño}
            precio={item.precioVenta}                    
            paquete={item.paquete}
            category={match.params.category}
            fotoCover={item.fotoCover}
            idx={item.indice}
        />
    )}            
  </ItemListContainer>
}

export default Catalogo;
