import React from 'react';
import styled from 'styled-components';
import {setFlex, colors} from '../../utilities';

const SalesBase = ({className}) => {
    return (
        <div className={className}>
        <h3>Venta de juegos infantiles</h3>
        <div className="box_container">
            <div className="box">
                <div className="icon"></div>
                <p className="box_title">Variedad</p>
                <p className="box_txt">Contamos con una amplia variedad de juegos inflables de mas de 50 mnodelos diferentes</p>
            </div>
            <div className="box">
                <div className="icon"></div>
                <p className="box_title">Calidad</p>
                <p className="box_txt">Calidad</p>
            </div>
            <div className="box">
                <div className="icon"></div>
                <p className="box_title">Confianza</p>
                <p className="box_txt">Confianza</p>
            </div>
            <div className="box">
                <div className="icon"></div>
                <p className="box_title">Forma de pago y facturación</p>
                <p className="box_txt">Forma de pago y facturación</p>
            </div>
        </div>
        </div>
    );
};

const Venta = styled(SalesBase)`    
    width: 95%;
    margin: 2rem auto;

    h3{
      background-color: red;
      color: ${colors.white_1};
      font-size: 2.5rem;
      text-align: center;      
    }

    .box_container{
      ${setFlex()};
      background-color: blue;
      padding: 1rem;
      text-align: center;

        .box{
            background-color: yellow;            
            width: 95%;

            .icon{
              background-color: green;              
              width: 95%;
            }

            .box_title{
              background-color: orange;              
              width: 95%;
            }

            .box_txt{
              background-color: yellowgreen;
              width: 95%;
            }
        }
    }
`;


export default Venta;
