import React, {useState} from 'react';
import styled from 'styled-components';
import {setFlex, colors, below, elevation, demotion} from '../utilities';

function Filter({className, category}) {

  const [checked, setChecked] = useState('');

  function handleChange(e){    
    if(e.target.checked){      
      setChecked(e.target.value);      
    }   
  }
  console.log(checked);  

  return (
    <div className={className}>      
      <div className="card">

      <div className="checkbox-container">
        <label className="checkbox-label">
          <input 
            type="checkbox"
            value="chico"
            onChange={handleChange}
            />
          <span className="checkbox-custom rectangular"></span>
        </label>
        <div className="input-title">Chico</div>
      </div>

      <div className="checkbox-container">
        <label className="checkbox-label">
          <input 
            type="checkbox"
            value="mediano"
            onChange={handleChange}
            />
          <span className="checkbox-custom rectangular"></span>
        </label>
        <div className="input-title">Mediano</div>
      </div>

      <div className="checkbox-container">
        <label className="checkbox-label">
          <input 
            type="checkbox"
            value="grande"
            onChange={handleChange}
            />
          <span className="checkbox-custom rectangular"></span>
        </label>
        <div className="input-title">Grande</div>
      </div>

      <div className="checkbox-container">
        <label className="checkbox-label">
          <input 
            type="checkbox"
            value="adulto"
            onChange={handleChange}
            />
          <span className="checkbox-custom rectangular"></span>
        </label>
        <div className="input-title">Adultos</div>
      </div>

      <div className="checkbox-container">
        <label className="checkbox-label">
          <input 
            type="checkbox"
            value="pr"
            onChange={handleChange}
            />
          <span className="checkbox-custom rectangular"></span>
        </label>
        <div className="input-title">Precio + </div>
      </div>

      </div>
    </div>
  );
}

const ItemFilter = styled(Filter)`
  width: 95%;  
  transform: scale(.7);

  .card {  
    margin: 1rem auto;
    margin-left: 2rem;
