import React from 'react';
import { Route } from 'react-router-dom';
import {PaqContainer} from '../../elements/Container';
import PaqList from '../items/PaqList';
import PaqDetail from '../items/PaqDetail';
import styled from 'styled-components';
import {setFlex, fixed, colors} from '../../utilities';


const PaqueteBase = ({className}) => {
    return (
        <>
            <div className={className}>
                <h1 className="title_paquetes">¡Arma tú Paquete!</h1>
                <p className="txt_paquetes">En la renta de cualquiera de nuestros inflables puedes complementar la diversion añadiendo cualquiera de los siguientes productos a un precio increible!...</p>
                <PaqContainer>
                {/* <SearchBar/> */}
                <div className="paquetes_list">
                    <Route exact path={`/paquetes`} component={PaqList}/>
                    <Route exact path={`/paquetes/:id`} component={PaqDetail}/>
                </div>
                </PaqContainer>
            </div>
        </>
    );
};

const Paquete = styled(PaqueteBase)`
margin-top: 5rem;
height: 100%;      
position: relative;
${setFlex({ x:"space-around", y:'flex-start' })};    
${fixed( )};

.title_paquetes{
    position: absolute;
    font-size: 3rem;
    text-align:center;
    font-weight: 700;
    ${setFlex()};
    letter-spacing: .4rem;
    color: ${colors.white_1};  
    text-shadow: 4px 4px 15px ${colors.blue_6};
}

.txt_paquetes{
    font-size: 1.2rem;
    letter-spacing: .2rem;
    text-align:center;
    line-height: 2.5rem;
    color: ${colors.white_1};
    margin: 6rem 0 0 0;
}


`

export default Paquete;
