import React from 'react';
import styled from 'styled-components';
import {AuthContainer} from '../../elements/Container';
import {setFlex, demotion, colors} from '../../utilities'

const SignupBase = ({className}) => {
  return (
    <AuthContainer>      
        <form action="" className={className}>          
            <label htmlFor=""  className="label">Nombre</label>
            <input type="text" name="name" placeholder="Ingresa tu nombre"/>

            <label htmlFor="" className="label">Email</label>
            <input type="email" name="email" placeholder="Ingresa tu correo"/>

            <label htmlFor="" className="label">Contraseña</label>
            <input type="text" placeholder="* * * * * * * *"/>  

            <label htmlFor="" className="label">Confirmar contraseña</label>
            <input type="text" placeholder="* * * * * * * *"/>            
        </form>      
    </AuthContainer>
  );
}

const Signup = styled(SignupBase)`    
  width: 90%;  
  height: auto;  
  ${demotion[3]};
  border-radius: .5rem;
  display: flex;
  flex-flow: column nowrap;



  label{
    color: ${colors.white_1};    
    font-size: .8rem;
    font-weight: 700;
    margin: .7rem;
    margin-left: 1rem;
  }

  input{
    width: 90%;
    height: 3rem;
    ${demotion[2]};
    border: none;
    background-color: transparent;
    border-radius: .5rem;
    margin: 0 auto;
  }

`;

export default Signup;
