import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, elevation, colors} from '../../utilities';
import {SliderContainer} from '../Container';
import Micro from '../svg/Micro.jsx';
import {SliderLink} from '../Button';


const Rockolas = ({className}) => {
  return (
<SliderContainer className={className}>        
      <div className="icon"><Micro size={40}/></div>      
      <h3 className="title slide_title">Rockolas</h3>
      <div className="text">
        <p className="txt">Pantalla touch, iluminación  más de 150 mil canciones y videos</p>    
      </div>            
      <SliderLink>
        <Link to={'/items/rockola'}>Explorar</Link> 
      </SliderLink>
</SliderContainer>
  );
}

const SlideRock = styled(Rockolas)`
  background-image: linear-gradient(to right, ${colors.blue_3}, ${colors.noryan_blue} );
  ${setFlex({ x:"", y:"" })};


  background-color: #FF7D9D;
background-size: 58px 58px;
background-position: 0px 2px, 4px 35px, 29px 31px, 33px 6px,
0px 36px, 4px 2px, 29px 6px, 33px 30px;
background-image:
linear-gradient(335deg, #C90032 23px, transparent 23px),
linear-gradient(155deg, #C90032 23px, transparent 23px),
linear-gradient(335deg, #C90032 23px, transparent 23px),
linear-gradient(155deg, #C90032 23px, transparent 23px),

linear-gradient(335deg, #C90032 10px, transparent 10px),
linear-gradient(155deg, #C90032 10px, transparent 10px),
linear-gradient(335deg, #C90032 10px, transparent 10px),
linear-gradient(155deg, #C90032 10px, transparent 10px);


`;

export default SlideRock;
