import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, below, colors, absolute} from '../../utilities';
import {SliderContainer} from '../Container';
import Castle from '../svg/Castle.jsx';
import {SliderLink} from '../Button';

const Inflables = ({className}) => {
  return (
    <SliderContainer className={className}>          
      <div className="icon"><Castle size={40}/></div> 
      <div className="img_box">            
        <h3 className="title">Juegos Inflables</h3>
        <img className="img" src="https://res.cloudinary.com/noryan/image/upload/v1595979077/foto_covers/circuito_junior.png" alt=""/>
      </div>

      <div className="text_box">
        <p className="txt">MÃ¡s de 60 modelos diferentes</p>
        <p className="txt">Desde $600 por evento</p>
      </div> 

      <div className="link">
        <SliderLink>
          <Link to={'/items/inflable'}>Expolorar</Link> 
        </SliderLink>
      </div>

    </SliderContainer>
  );
}

const SlideInflables = styled(Inflables)`  
  background-image: linear-gradient(to right, ${colors.blue_3}, ${colors.noryan_blue});
  position: relative;

  .img_box{
    height: 50%;    
    ${setFlex({x:"space-around"})};    
    margin: 0rem 1rem;

    .title{
      width: 50%;
      margin: 0rem auto;
      margin-left: 2rem;      
    }

    img{
      margin-top: 1rem;
      height: 100%;
      background-color: yellowgreen;
    }
  }

  .text_box{    
    margin: 0rem auto;

    .txt{
      line-height:2.5rem;
    }
  }


  ${below.md`
    flex-flow: column nowrap;

    .title{            
      width: 100%;
      margin: 0rem auto;
      margin-left: 2rem;
      background-color: yellow;
    }

    .img_box{          
      ${setFlex()};

      img{
        height: 90%;
      }
    }

    .text{      
      margin-top: 2rem;
      line-height: 1rem;

      .txt{
        margin-top: .5rem;
        font-size: 2rem;
      } 
    }

    .link{
      position: static;
      margin: 0 auto;
      margin-top: 3rem;
    }
  `}

  ${below.sm`
    .title{           
      font-size: 1.7rem;
      padding-left: 25%; 
    }


  `}

${below.xsm`
    .title{           
      font-size: 1.4rem;
      padding-left: 30%; 
    }
  `}

`;

export default SlideInflables;
