import React from 'react'
import {Link} from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex, below, absolute, demotion, elevation} from '../utilities';
import Back from '../elements/svg/Back';
import AliceCarousel from 'react-alice-carousel';


function Card({className, nombre, desc, observaciones, precio, medidas, fotoCover, fotos, indice, back}) {
    return (                		    
      <div className={className} >        
          <div onClick={back} className="icon"><Back/></div>
    <h2 className="title">{nombre}</h2>
        <div className="container">
          {/* ---  DESCRIPTION --- */}
          <div className="dtl__box">     
            <h3>No. Producto: {indice}</h3>
            <h4>Descripción</h4>
            <div className="description">              
              <div className="desc_txt">{desc}</div>              
            </div>
            <h4>Medidas</h4>                   
              <div className="medidas">
              <span className="ms">Ancho: <p>{medidas && medidas[0]}m</p> </span>
              <span className="ms">Largo: <p>{medidas && medidas[1]}m</p> </span>
              <span className="ms">Alto: <p>{medidas && medidas[2]}m</p> </span>
              <div className="precio">
                <p>Costo por día: <span className="price">${precio}</span></p>
              </div>
              </div>                                                       
            <p className="observaciones">{observaciones}</p>
          </div>                  
          {/* ---  PICTURES --- 

          <div className="foto_cover">
              <img src={fotoCover} alt={nombre} className="card__img"/>
            </div>
            <div className="fotos">

            {fotos && fotos.map( (foto, idx) => 
              <img  key={idx}  src={foto}  alt="" className="card__img"/>
            )}

            </div>
          */}
          <div className="product-pics">

          <AliceCarousel
            items={fotos}
            responsive={this.responsive}
            autoPlayInterval={2000}
            autoPlayDirection="rtl"
            autoPlay={true}
            fadeOutAnimation={true}
            mouseTrackingEnabled={true}
            disableAutoPlayOnAction={true}
          />        

          </div>          
        </div>
        <Link to='/contacto' className="round-black-btn">Reservar</Link>
      </div>        


    )
}

const ItemDetailCard = styled(Card)`
  width: 100%;
  margin: 0rem auto;
  padding: 1rem 1.5rem;
  color: ${colors.white_1};
  position: relative;

  .icon{
    ${absolute({ x:"1.5rem", y:"1rem" })};
    cursor: pointer;
  }

  h2{
    width: 100%;
    text-align:center;
    text-transform: uppercase;
    font-size: 1.5rem;
  }

  .container{
    ${setFlex({ x:"space-between", y:'flex-start' })};   

    .dtl__box{        
      width: 48%;
      font-size: .8rem;    

    h4{
      font-size: 1.2rem;    
      margin: 1rem 0rem;      
    }  

    .description{
      overflow: auto;
      height: 25vh;
      padding: .9rem;
      border-radius: .7rem;
      ${demotion[1]};

      .desc_txt{
        line-height: 1.5rem;
        text-align: justify;
        margin-bottom: 3rem;
        font-size: 1rem;
        font-weight: 500;
      }
    }   

    .medidas{
      ${setFlex({ x:"flex-start"})};
      width: 100%;
      line-height: 1.5rem;      
      padding: 0rem 1rem;
      border-radius: .8rem;
      ${elevation[1]}

      .ms{        
        width: 33%;
        font-weight: 500;
        font-size: 1rem;

        p{
          padding: 0rem;
          margin: 0rem; 
          font-weight: normal;          
        }
      }  
    }      

    .precio{
      width: 100%;
      font-size: 1rem;      
      text-align: right;

      p {                    
        letter-spacing: .1rem;
        .price{
          font-weight: bolder;          
        }
      }
    }   

    .observaciones{      
      line-height: 1.1rem;
      margin-top: 2rem; 
      font-weight: 500;   
    }
  } /* finish dtl_box */

    .product-pics{    
      border: white solid 1px;
      width: 48%;
      height: 35vh;    

      .foto_cover{
        img{
          height: 30vh;
        }
      }

      img{
        height: 2.5rem;
      }

    } /* finish product-pics */
  }


  ${below.md`
    padding: .2rem 1rem;

    .icon{
      ${absolute({ x:"1rem", y:".5rem" })};      
    }

    .container{
      flex-direction: column-reverse;    

      .dtl__box{
        width: 100%;

        .description{
          overflow: auto;
          height: 30vh;
          padding: .3rem;
          border-radius: .7rem;
          ${demotion[1]};

          .desc_txt{
            line-height: 1.5rem;
            text-align: justify;
            margin-bottom: 3rem;
            font-size: 1rem;
            font-weight: 500;
          }
        } 

        .medidas{
          padding: 0rem .5rem;

          .ms{        
            width: 50%;
            font-weight: 500;
            font-size: .8rem;

            p{
              padding: 0rem;
              margin: 0rem; 
              font-weight: normal;          
            }
          }
        }
      }
      .product-pics{

        .foto_cover{
          height: 8rem;
        }
          width: 100%
      }
    }
  `}
`;

export default ItemDetailCard
