    overflow: auto;

`;

export const RentContainer = styled.div`
    position: relative;
    width: 100%;    
    ${setFlex({ x:"space-around", y:'flex-start' })};    

    .item_list{ /*  */
        position: relative;
        width: 85%;    
        margin: 1rem 1rem;
        margin-right: 1rem;
        overflow: auto;  
    }

