import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex, below } from '../../utilities';
import {SliderContainer} from '../Container';
import Fut from '../svg/Fut.jsx';
import {SliderLink} from '../Button';

const Futbolito = ({className}) => {
  return (
    <SliderContainer className={className}>    
      <div className="icon"><Fut size={40}/></div>
      <h3 className="title">Futbolitos</h3>      

      <div className="img_box">
        <p className="txt">Futbolitos tama√±o infantil y para adulto </p>
        <SliderLink>
          <Link to={'/items/futbolito'}>Explorar</Link> 
        </SliderLink>
        <img className="img" src="https://cdn1.bbcode0.com/uploads/2020/8/8/be8f6ade2d920d03efc5e59c36dc62dd-full.png" alt=""/>
      </div>
    </SliderContainer>
  );
}

const SlideFut = styled(Futbolito)`
  background-image: linear-gradient(to right, ${colors.blue_6}, ${colors.blue_2});

  .title{
    width: auto;
    margin: 1rem auto;
    padding-top: 1.5rem;
    padding-left: 10%;    
  }

  .img_box{
    background-color: red;
    height: 100%;     
    ${setFlex({x:"flex-end",y:"flex-start"})};
    flex-flow: column nowrap;

    .img{      
      height: 100%;
      width: 100%;
      margin: 0 auto;

    }

    .txt{
      background-color: yellow;
      margin-top: -1rem;
      font-size: 2rem;      
      text-align: center;
    }

    a{
      height: 3rem;
      padding-top: .7rem;
    }
  }

  ${below.md`
    .title{      
      padding-left: 15%;    
    }

    .img_box{
      margin-top: 5rem;
      height: 60%;     
      ${setFlex({x:"flex-end",y:"flex-end"})};
      flex-flow: column nowrap;    

      .txt{
        margin-top: -1rem;
        font-size: 2rem;      
        text-align: center;
      }
    }
  `}

  ${below.sm`
    .title{      
      padding-left: 25%;
    }

    .img_box{
      margin-top: 7rem;
      height: 60%;     
      ${setFlex({x:"flex-end",y:"flex-end"})};
      flex-flow: column nowrap;

      .img{      
        height: 80%;
        margin: 0 auto;
        transform: scale(1);
      }

      .txt{        
        font-size: 1.7rem;      
        text-align: center;
        padding: 0rem 1rem;
      }
    }
  `}


`;

export default SlideFut;

