import React from 'react'
import {Link} from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex} from '../utilities';

function Card({className, nombre, desc, observaciones, precio, medidas, fotoCover, fotos, back}) {
    return (
      <div className={className}>            		    
        <div className="product-dtl">

          <h2 onClick={back}>{nombre}</h2>

          <div className="description">
            <h4>Descripci√≥n</h4>
            <div className="desc_txt">{desc}</div>                
          </div>


          <div className="product-pics">
          <div className="foto_cover">
            <img src={fotoCover} alt={nombre} className="card__img"/>
          </div>
          <div className="fotos">
          {fotos && fotos.map( (foto, idx) => 
            <img
              key={idx}
                src={foto}
                alt="" 
                className="card__img"                  
            />
          )}
          </div>          
        </div>

          <div className="dtl__box">                                                 
            <div className="medidas">
              <h4>Medidas</h4>
              <ul>
                {medidas && medidas.map( (medida, idx) => {
                  return <li key={idx}>{medida}</li>;
                })}
              </ul>
            </div>              
            <div className="precio"><p>$ {precio}</p></div>
          </div>

          <p className="observaciones">{observaciones}</p>

			  </div>        

        <Link to='/contacto' className="round-black-btn">Reservar</Link>
      </div>
    )
}

const ItemDetailCard = styled(Card)`
    color: ${colors.white_1};
    ${setFlex({ x:"space-between", y:"felx-start" })};
    font-size: .75rem;
    background-color: yellow;
    width: 95%;

    .product-dtl {
	    width: 100%;
      margin: 1rem auto;      
      background-color: blue;

      h2{
        text-align:center;
        text-transform: uppercase;
        font-size: 1.2rem;        
      }

      .description{
        width: 50%;
        background-color: purple;

        .desc_txt{
          line-height: 1.1rem;
          text-align: justify;
        }
      }  

      .observaciones{        
        line-height: 1.1rem;
        background-color: green;
      }

      .dtl__box{
        ${setFlex({ x:"space-between" })};
        background-color: red;

        .precio{
          width: 50%;
          ${setFlex({ x:"flex-end", y:"flex-end" })};
          margin-right: 3rem;

          p {
            font-size: 1rem;
            font-weight: bolder;
            letter-spacing: .1rem;
          }   
        }        
      }      

    }

    .product-pics{
      background-color: orange;   
      width: 50%;      
      margin-top: 2rem;
      margin-right: 2rem;

      img{
        height: 4rem;
      }
    }

`;

export default ItemDetailCard
