import React from 'react';
import styled from 'styled-components';
import {setFlex, elevation, colors} from '../../utilities';
import {SliderContainer} from '../Container';
import Rockola from '../svg/Rockola.jsx';

const Rockolas = ({className}) => {
  return (
<SliderContainer className={className}>        
      <Rockola size={150}/>
      <h3>Rockolas y Karaoke</h3>
      <div className="text">
        <p>Pantalla touch, iluminación  más de 150 mil canciones y videos</p>    
      </div>            
</SliderContainer>
  );
}

const SlideRock = styled(Rockolas)`
  background-image: linear-gradient(to right, ${colors.noryan_blue}, ${colors.noryan_prpl} );

`;

export default SlideRock;
