import React from 'react';
import styled from 'styled-components';
import {setFlex, colors, below, fixed} from '../utilities';
import NavbarList from '../elements/NavbarList';
import Burger from '../elements/Burger';

function NavBarBase({ className, open }) {
    return (
        <div className={className}>        
            <NavbarList/>
            <Burger/>
        </div>
    )
}

const NavBar = styled(NavBarBase)` 
    position: relative;
    width: 100%;
    background-color: ${colors.blue_1};
    ${setFlex({x:"space-around"})};    

    ${below.md`             

        .nav_list{
            flex-flow: column nowrap;            
            height: 100vh; 
            width: 100%;
            ${fixed({x:"-1rem", y:"3.5rem" })}

            transform: ${({ open }) => open ? 'translateX(0)' : 'translateX(100%)'};
            transition: transform 0.3s ease-in-out;  
        }

        .nav_item{
            margin 1.4rem auto;
        }

        .nav_link{ 
            font-size: 1rem;
            color: ${colors.white_1};
            text-decoration: none;
            display: block;          
        } 
    `};


    /* Active Style */
    .nav_list .active_link{ 
        font-weight: bold;
        font-size: 1.1rem;
        color: ${colors.blue_3};
    }

`;

export default NavBar;
