import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, below, absolute, colors} from '../../utilities';
import {SliderContainer} from '../Container';
import {SliderLink} from '../Button';

const Noryan = ({className}) => {
  return (
    <SliderContainer className={className}>
      <div className="txt_box">
        <h1 className="title">Inflables, Futbolitos, Rockolas, Maquinitas y mucho más ...!</h1>      
        <p className="txt">CDMX, Estado de México & Cuernavaca</p>
      </div>
      <div className="link">
        <SliderLink>
          <Link to={'/items/inflable'} >Explorar</Link>  
        </SliderLink>
      </div>               
      <div className="logo"></div> 
    </SliderContainer>
  );
}

const SlideNoryan = styled(Noryan)`  
  ${setFlex({x:"space-around"})};

  .txt_box{
    text-align: center;    

    .title{
      margin: 1rem auto;
      font-size:3rem;
      font-weight: bolder;    
      text-shadow: 6px 6px 12px rgba(0,0,0,0.5),
              -6px -6px 12px rgb(33, 63, 232, 0.3);
    }

    .txt{
      margin: 1rem auto;
      padding: 1rem 0;
      font-size:2.5rem;
      text-shadow: 6px 6px 12px rgba(0,0,0,0.5),
              -6px -6px 12px rgb(33, 63, 232, 0.3);
    }
  }

  .link{

  }

  .logo{    
    ${absolute()};
    background-image: url('https://cdn1.bbcode0.com/uploads/2020/8/13/0c22dca1ca538743a85061abdf87fdaa-full.png');
    background-position: center;
    background-repeat: no-repeat;
    width: 100%;
    height: 70vh;
    margin-top: 1rem;
    z-index: -1;
  }

  ${below.md`

    .title{    
      ${setFlex({y:"flex-start"})};
      font-size: 2rem;
      padding: 2rem 0 1rem;
      line-height: 2.1rem;
    }

    .logo{
      width: 100%;
      transform: scale(.8);
      margin-top: 7rem;
    }    
  `}

  ${below.sm`    
    .title{      
      width: 90%;
      ${setFlex({y:"flex-start"})};
      font-size: 1.7rem;
      line-height: 2rem;
      margin-top: 2rem;
    }

    .logo{      
      transform: scale(.5);
      margin-top: 2rem;
    }

    .txt{
      width: 90%;
      font-size: 1.3rem;
      font-weight: 600;
      letter-spacing: .1rem;
      margin-bottom: 4rem;
    }
  `}

${below.xsm`

    .title{      
      width: 90%;
      ${setFlex({y:"flex-start"})};
      font-size: 1.4rem;
      margin-top: 0rem;
    }

    .logo{      
      background-image: url('https://i.postimg.cc/tR2VhhzY/turtle-sm.png');
      width: 100%;
      transform: scale(1);
      display: block;
      margin-top: 2rem;
    }

    .txt{
      width: 90%;
      font-size: 1.2rem;
      font-weight: 600;
      letter-spacing: .1rem;
      height: 2rem;
      margin-top: 0rem;
    }
  `}
`;

export default SlideNoryan;
