import React, {useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import ItemListCard from '../../layout/ItemListCard';
import {ItemListContainer} from '../../elements/Container';
import {listItems} from '../../actions/itemActions';
import SearchBar from '../../layout/SearchBar';

const ItemList = ({match }) => {

    // we need to create a state for the search and for the filtering & sort

    const itemList = useSelector( state => state.items );
    const {items} = itemList;
    const dispatch = useDispatch();

    useEffect( () => {
        dispatch(listItems(match.params.category))
    },[dispatch, match.params.category]);

    return (
        <>
          <SearchBar category={match.params.category}/>
          <ItemListContainer>
            <h3>{match.params.category}</h3>
            {items && items.map( (item,idx) =>              
                <ItemListCard                     
                    key={idx}
                    id={item.id}
                    nombre={item.nombre}
                    tamaño={item.tamaño}
                    precio={item.precio}                    
                    paquete={item.paquete}
                    category={match.params.category}
                    fotoCover={item.fotoCover}
                />
            )}            
          </ItemListContainer>
        </>
    );
};

export default ItemList;
