import React from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, colors} from '../utilities';

function NavBarBase({ className }) {
    return (
        <div className={className}>        
            <ul className="nav_list">
                <li> <NavLink activeClassName="active_link" className="nav_link" exact to="/items">Catálogo de renta</NavLink></li>
                <li> <NavLink activeClassName="active_link" className="nav_link" exact to="/paquetes">Renta de paquetes</NavLink></li>
                <li> <NavLink activeClassName="active_link" className="nav_link" exact to="/venta">Catálogo de venta</NavLink></li>
                <li> <NavLink activeClassName="active_link" className="nav_link" exact to="/negocio">Inicia tu negocio</NavLink></li>
                <li> <NavLink activeClassName="active_link" className="nav_link" exact to="/condiciones">Condiciones</NavLink></li>
                <li> <NavLink activeClassName="active_link" className="nav_link" exact to="/nosotros">Nosotros</NavLink></li>
                <li> <NavLink activeClassName="active_link" className="nav_link" exact to="/contacto">Contacto</NavLink></li>
            </ul>    
        </div>
    )
}

const NavBar = styled(NavBarBase)`     
    width: 100%;
    background-color: ${colors.blue_1};
    ${setFlex({x:"space-around"})};    

    .nav_list{
      position: relative;
	  list-style: none;
      margin: 1rem auto;
      display: flex;

      .nav_link{
        font-size: 1rem;
        color: ${colors.white_1};
        text-decoration: none;
        display: block;
        margin: 0 1.5rem;
      }
    }

    .nav_list .active_link{
        font-weight: bold;
        font-size: 1.1rem;
        color: ${colors.blue_3};
    }

`;

export default NavBar;
