import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { colors, demotion, elevation, setFlex, fixed } from '../utilities';
import Anticipo from '../elements/svg/Anticipo';
import Cash from '../elements/svg/Cash';
import Credit from '../elements/svg/Credit';
import Reject from '../elements/svg/Reject';


const CondicionesPagoBase = ({className}) => {
    return (
        <div className={className}>
            <div className="advance">
                <Anticipo className="payment_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-advance">Se solicitará anticipo del 50% en servicios con monto total a pagar mayor a $1,000.</li>
                    <li className="txt-cond-advance">Se solicitará anticipo del 50% cuando por las características del evento así se requiera.</li>
                </div>
            </div>
            <div className="method">
                <Cash className="payment_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-method">Puede pagar en efectivo o por transferencia electrónica al momento de la instalción.</li>
                    <li className="txt-cond-method">Aceptamos pagos con tarjeta de crédito incluyendo American Express o vía PayPal.</li>
                    <li className="txt-cond-method">Es indispensable liquidar el servicio al momento de la instalción. No aceptamos cheques.</li>

                </div>
            </div>
            <div className="card">
                <Credit className="payment_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-card">Debe indicar su forma de pago al momento de la contratación o a más tardar un día antes de su evento.</li>
                </div>
            </div>
            <div className="reject">
                <Reject className="payment_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-reject">Si al realizar el cargo a su tarjeta el banco lo rechaza el pago deberá hacerse en efectivo.</li>
                    <li className="txt-cond-reject">Es indispensable contar con buena recepción de señal celular para poder realizar el pago con tarjeta.</li>
                </div>
            </div>


        </div>
    );
};

const CondicionesPago = styled(CondicionesPagoBase)`
    position: relative;
    width: 100%;
    margin-top: 8rem;


    .payment_icon{
        display: inline-block;      
    }

    .list{
        display: inline-block;
        margin-left: 0.5rem;
        list-style-type: square;
    }


    .advance{
        width: 95%;
        height: 5rem;
        margin: 1rem 0 2.5rem 3rem;
    }

    .method{
        width: 95%;
        height: 5rem; 
        margin: 0.5rem 0 2.5rem 3rem;
    }

    .card{
        width: 95%;
        height: 5rem;        
        margin: 0.5rem 0 2.5rem 3rem;
    }

    .reject{
        width: 95%;
        height: 5rem;      
        margin: 0.5rem 0 2.5rem 3rem;
    }


    .txt-cond-advance{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }

    .txt-cond-method{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }

    .txt-cond-card{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;        
    }

    .txt-cond-reject{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }


`



export default CondicionesPago;
