import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex, below } from '../../utilities';
import {SliderContainer} from '../Container';
import Fut from '../svg/Fut.jsx';
import {SliderLink} from '../Button';

const Futbolito = ({className}) => {
  return (
    <SliderContainer className={className}>    
      <div className="icon"><Fut size={40}/></div>
      <h3 className="title">Futbolitos</h3>    

      <div className="fut_box">
      <img className="img" src="https://mundialgames.com/images/stories/virtuemart/product/residencial%20olmo5.png" alt=""/>
        <div className="img_box">          
        </div>
        <div className="text">
          <p className="txt">Infantil y adultos</p>
          <SliderLink>
            <Link to={'/items/futbolito'}>Explorar</Link> 
          </SliderLink>
        </div>
      </div>

    </SliderContainer>
  );
}

const SlideFut = styled(Futbolito)`
  background-image: linear-gradient(to right, ${colors.blue_6}, ${colors.blue_2});  

  .title{
    width: auto;
    margin: 1rem auto;
    padding-top: 1.5rem;
    padding-left: 15%;    
  }  

  .fut_box{
    ${setFlex({x:"space-around"})};
    height: 60%;    

    img{
        margin-top: 2rem;        
    }


    .text{      
      width: 50%;

      .txt{
        text-align: center;
      }
    }
  }

  ${below.md`
    .title{
      width: auto;
      margin: 1rem auto;
      padding-top: 1.5rem;
      padding-left: 15%;    
    }  

    .fut_box{
      ${setFlex()};
      height: 40%;    

      .img_box{
        margin-left: 2rem;

        img{
          margin-top: 2rem;        
        }
      }

      .text{      
        width: 50%;

        .txt{
          text-align: center;
        }
      }
    }
  `}
`;

export default SlideFut;
