import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import ReactPlayer from 'react-player';
import {setFlex, below, colors, absolute} from '../../utilities';
import {SliderContainer} from '../Container';
import {SliderLink} from '../Button';

const Noryan = ({className}) => {
  return (
    <SliderContainer className={className}>
       <div className="video">
        <ReactPlayer
          url='https://res.cloudinary.com/noryan/video/upload/v1595382193/colages_home/aqua_maze_video_home.mp4'
          className='react-player'
          playing='true'
          loop='true'
          muted='true'
          width='70%'
          height='100%'
        />
      </div>
      <h1 className="title">Inflables, Futbolitos, Rockolas, Maquinitas y mucho más ...!</h1>      
      <p className="txt">CDMX, Estado de México & Cuernavaca</p>
      <SliderLink>
        <Link to={'/items/inflable'} >Explorar</Link>  
      </SliderLink>
      <div className="logo"></div>     
    </SliderContainer>
  );
}

const SlideNoryan = styled(Noryan)`

  text-align: center;

  .video{    
    opacity: 0.3;
    ${absolute()};
    z-index: -1;
  }

  .title{    
    padding: 1.5rem 0;
    margin: 0 auto;    
  }

  .logo{    
    background-image: url('https://cdn1.bbcode0.com/uploads/2020/7/13/fcae2fb3af62db254607c2671ed3176b-full.png');
    background-position: center;
    background-repeat: no-repeat;
    width: 100%;
    height: 20%;
    margin-top: 1rem;    
    transform: scale(.8);
  }

  .txt{
    padding: 1rem 0;
  }

  ${below.md`

    .title{    
      ${setFlex({y:"flex-start"})};
      font-size: 2rem;
      padding: 2rem 0 1rem;
      line-height: 2.1rem;
    }

    .logo{
      width: 100%;
      transform: scale(.8);
      margin-top: 7rem;
    }    
  `}

  ${below.sm`    
    .title{      
      width: 90%;
      ${setFlex({y:"flex-start"})};
      font-size: 1.7rem;
      line-height: 2rem;
      margin-top: 2rem;
    }

    .logo{      
      transform: scale(.5);
      margin-top: 2rem;
    }

    .txt{
      width: 90%;
      font-size: 1.3rem;
      font-weight: 600;
      letter-spacing: .1rem;
      margin-bottom: 4rem;
    }
  `}

${below.xsm`

    .title{      
      width: 90%;
      ${setFlex({y:"flex-start"})};
      font-size: 1.5rem;      
    }

    .logo{      
      background-image: url('https://i.postimg.cc/tR2VhhzY/turtle-sm.png');
      width: 100%;
      transform: scale(1);
      display: block;
      margin-top: 2rem;
    }

    .txt{
      width: 90%;
      font-size: 1.2rem;
      font-weight: 600;
      letter-spacing: .3rem;
    }
  `}
`;

export default SlideNoryan;
