import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex } from '../../utilities';
import {SliderContainer} from '../Container';
import Fut from '../svg/Fut.jsx';
import {SliderLink} from '../Button';

const Futbolito = ({className}) => {
  return (
    <SliderContainer className={className}>    
      <div className="icon"><Fut size={40}/></div>
      <h3 className="title">Futbolitos</h3>      
      <div className="img_box"> 
        {/*<img className="img" src="https://mundialgames.com/images/stories/virtuemart/product/residencial%20olmo5.png" alt=""/>*/}        
        <p className="txt">Infantil y adultos</p>
      <SliderLink>
          <Link to={'/items/futbolito'}>Explorar</Link> 
       </SliderLink>
      </div>      
    </SliderContainer>
  );
}

const SlideFut = styled(Futbolito)`
  background-image: linear-gradient(to right, ${colors.blue_6}, ${colors.blue_2});   

  .title{
    width: auto;
    margin: 1rem auto;
    padding-top: 1.5rem;
    padding-left: 10%;    
  }

  .img_box{
    height: 50%;
    background-image: url("https://mundialgames.com/images/stories/virtuemart/product/residencial%20olmo5.png");
    background-position: center;
    background-repeat: no-repeat;    

    img{
      transform: scale(.8);
    }
  }

`;

export default SlideFut;
