import React, { useState } from "react";
import styled from 'styled-components';
// import SearchItemList from '../components/items/SearchItemList';
//import {setFlex, fixed, colors } from '../utilities';

function SearchBarBase({ className }) {

    // state to track search keyword
    const [searchKeyword, setsearchKeyword] = useState('');

    function handleSubmit (e) {
        e.preventDefault();
        // trigger filter dispatch       
    }

    return (
    <div className={className}>
        <form onSubmit={handleSubmit}>                             
            <input                
                type="text"
                placeholder="Busca un producto..."
                name="searchKeyword"
                onChange={ e => searchKeyword(e.target.value)}
            />       
        </form>
        <select name="sortOrder" id="">
            <option value="">ordena precio por</option>
            <option value="">precio +</option>
            <option value="">precio -</option>
            <option value=""> - $1,000 </option>
            <option value=""> + $1,000</option>
            <option value=""> + $3,000</option>
            <option value=""> + $5,000</option>
        </select>
    </div>
    );
}

const SearchBar = styled(SearchBarBase)`
    position: relative;
    width: 20%;
    height: 3rem;
    border-radius: 50%;
`;


export default SearchBar;

