import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, elevation, colors} from '../../utilities';
import {SliderContainer} from '../Container';
import Micro from '../svg/Micro.jsx';
import {SliderLink} from '../Button';


const Rockolas = ({className}) => {
  return (
<SliderContainer className={className}>        
      <div className="icon"><Micro size={40}/></div>      
      <h3 className="title slide_title">Rockolas</h3>
      <div className="text">
        <p className="txt">Pantalla touch, iluminación  más de 150 mil canciones y videos</p>    
      </div>            
      <SliderLink>
        <Link to={'/items/rockola'}>Explorar</Link> 
      </SliderLink>
</SliderContainer>
  );
}

const SlideRock = styled(Rockolas)`
  background-image: linear-gradient(to right, ${colors.blue_3}, ${colors.noryan_blue} );
  ${setFlex({ x:"", y:"" })};

  background-color: transparent;
  background-image: url("data:image/svg+xml,%3Csvg width='120' height='120' viewBox='0 0 80 80' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23213fe8' fill-opacity='0.14'%3E%3Cpath d='M50 50c0-5.523 4.477-10 10-10s10 4.477 10 10-4.477 10-10 10c0 5.523-4.477 10-10 10s-10-4.477-10-10 4.477-10 10-10zM10 10c0-5.523 4.477-10 10-10s10 4.477 10 10-4.477 10-10 10c0 5.523-4.477 10-10 10S0 25.523 0 20s4.477-10 10-10zm10 8c4.418 0 8-3.582 8-8s-3.582-8-8-8-8 3.582-8 8 3.582 8 8 8zm40 40c4.418 0 8-3.582 8-8s-3.582-8-8-8-8 3.582-8 8 3.582 8 8 8z' /%3E%3C/g%3E%3C/g%3E%3C/svg%3E");

`;

export default SlideRock;
