import React from 'react';
import styled from 'styled-components';
import {setFlex, fixed, colors, below} from '../utilities';
import { Whats, Phone } from '../elements/Icons';
import { TopBarButton } from '../elements/Button';
import { NavLink } from 'react-router-dom';


function TopBarBase({ className }) {
    return (
        <div className={className}>            
            <NavLink className="nav_link" exact to="/">
              <img className="logo_top" src="https://i.postimg.cc/vHM2Xfwk/footer-logo.png" alt="logo"/>
            </NavLink>
            <div className="contact_top">
              <a target="blank" href="https://api.whatsapp.com/send?phone=525536150824&amp;text=Hola,%20me%20contacto%20por%20la%20renta%20de%20juegos." className="contact_numbers"><Whats  size="25"/>55 - 3615 - 0824</a>
            </div>
            <div className="call_top">
              <a target="blank" href="tel:54457537" className="contact_numbers"><Phone  size="25"/>5445 - 7537</a>
            </div>
            <div className="button_top">
              <TopBarButton>
                <span>Iniciar sesi√≥n</span>
              </TopBarButton>
            </div>
        </div>
    )
}

const TopBar = styled(TopBarBase)`
    position: fixed;
    width: 100%; 
    height: 3rem;   
    background-color: ${colors.blue_2};  
    // margin-bottom: 3rem;
    padding-bottom: 0.5rem;    
    ${fixed( )};
    ${setFlex({ x:"space-between", y:"flex-end" })};

    .logo_top{
      // width: 30%;
      height: 3em;      
      transform: scale(1.1);
      margin-left: 3rem;
      margin-top: .7rem;

    }

    .nav_link{
      width: 15%;
      margin-right: 18rem;

    }

    .contact_top{
      width: 25%;
      margin-top: .4rem;   
      margin-left: 22rem;
      transform: scale(1.3);
    }

    .contact_numbers{
      color: ${colors.white_1};  
      text-decoration: none;
      font-size: 160%;

      ${below.lg`
        width: 80%;
    `}
    }

    .call_top{
        width: 25%;
        margin-top: .4rem;   
        margin-left: 9rem;
        transform: scale(1.3);
    }

    .button_top{
      width: 25%;      
      margin-top: .2rem;
      margin-right: 3rem;   
      ${setFlex({ x:"flex-end" })};    
    }

`;


export default TopBar;
