import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { colors, demotion, elevation, setFlex, below, above, fixed } from '../utilities';
import Castle from '../elements/svg/Castle';
import Fut from '../elements/svg/Fut';
import Arcade from '../elements/svg/Arcade';
import Rockola from '../elements/svg/Rockola';
import Jumping from '../elements/svg/Jumping';
import Photo from '../elements/svg/Photo';

function CategoryMenu({ className}) {

    return (
        <div className={className}>      
        <div className="logo_menu"></div>      
          <ul className="nav_list">
          <li key="inflable" className="nav_item">
            <NavLink className="nav_link" activeClassName="active_link" exact to={`/items/inflable`}>
              <Castle size={35}/> 
              <p className="nav_txt">inflable</p>
            </NavLink>
          </li>
          <li key="futbolito" className="nav_item">
            <NavLink className="nav_link" activeClassName="active_link" exact to={`/items/futbolito`}>          
              <Fut size={35}/>
              <p className="nav_txt">futbolito</p>
            </NavLink>
          </li>
          <li key="maquinita" className="nav_item">
            <NavLink className="nav_link" activeClassName="active_link" exact to={`/items/maquinita`}>          
              <Arcade size={35}/>
              <p className="nav_txt">maquinita</p>
            </NavLink>
          </li>
          <li key="rockola" className="nav_item">
            <NavLink className="nav_link" activeClassName="active_link" exact to={`/items/rockola`}>          
              <Rockola size={35}/>
              <p className="nav_txt">rockola</p>
            </NavLink>
          </li>
          <li key="jumping" className="nav_item">
            <NavLink className="nav_link" activeClassName="active_link" exact to={`/items/jumping`}>
              <Jumping size={35}/>
              <p className="nav_txt">jumping</p>
            </NavLink>
          </li>
          <li key="foto" className="nav_item">
            <NavLink className="nav_link" activeClassName="active_link" exact to={`/items/foto`}>          
              <Photo size={35}/>
              <p className="nav_txt">foto</p>
            </NavLink>
          </li>          
        </ul>             
        </div>
    )
}

const Menu = styled(CategoryMenu)`
    width: 15vw;
    height: 65vh;
    border-radius: 1.5rem;
    ${demotion[3]};
    position: relative;
    margin-top: 1rem;
    margin-left: 1rem;

    .logo_menu{
      position: relative;
      background-image: url('https://i.postimg.cc/tR2VhhzY/turtle-sm.png');
      background-position: center;
      background-repeat: no-repeat;
      width: 100%;
      height: 4rem;
      margin-top: .5rem;
      transform: scale(.6); 
    }    

    .nav_list{
      position: relative;
	    list-style: none;
      margin: 1rem 0 0 2rem;
      padding: 0;
      display: block; 
      flex-flow: column nowrap;  

      .nav_item{
        position: relative;        
        height: 2.5rem;        
        display: block;        
        border-top-left-radius: .5rem;
        border-bottom-left-radius: .5rem;
        ${setFlex({ x:"flex-start"})};

        &:hover{
            background-color: ${colors.white_trans};
            ${elevation[3]};
        }

        .nav_link{
          position: relative;
          width: 100%;
          padding: 1rem;
          color: ${colors.white_1};
          text-decoration: none;
          text-transform: capitalize;
          font-size: .8rem;
          font-weight: bold;
          cursor: pointer;
          ${setFlex({ x:"space-around" })};          

          .nav_txt{
            padding-left: 1.5rem;          
            width: 100%;
          }        

        }
      }
    }

    .active_link{
      position: relative;        
      height: 2.5rem;        
      display: block;
      cursor: pointer;
      border-top-left-radius: .5rem;
      border-bottom-left-radius: .5rem;
      background-color: ${colors.white_trans};
      ${elevation[3]};
    }

    ${below.md`      
      width: 90%;
      height: 3.5rem;   
      ${demotion[0]};      
      margin: 3rem .5rem;
      border-radius: .8rem;
      overflow: auto;

      .logo_menu{
        display: none;
      }


      .nav_list{ 
       width:100%; 
       background-color: red;            
	     list-style: none;       
       padding: 0;
       display: flex;
       flex-flow: row nowrap;
       justify-content: space-around;
       margin: 0; 

       .nav_icon{
         color: red;
       }

       .nav_item .nav_link .nav_txt{
        display: none;
       }

       .active_link{
        border-radius: .5rem;        
      }

    `}

    ${above.lg`height: 75vh;`}  
    @media all and (max-width: 1080px) and (min-width: 768px) and (orientation: portrait){    
      width: 25%;
      height: 75vh;
    }

    /*

Version 1
    ${below.md`
      width: 95%;
      height: auto;
      border-radius: 1.5rem;      
      margin: 3rem auto;
      ${demotion[3]};      
      ${setFlex({ y:"flex-start"})};

      .logo_menu{
        display: none;
      }     

      .nav_list{
        width: 100%;
        margin: 0;     

       .nav_item{
         margin: 2.5rem 0;
         ${setFlex()};         

         .nav_link{          
          font-size: 1.3rem;
          letter-spacing: 10px;
          padding-left: 2rem;

          .nav_txt{
            padding-left: 2.5rem;
          }       
         }
        }

        .active_link{                              
          display: block;
          cursor: pointer;
          border-radius: .5rem;          
        }
    `}
 */
`;

export default Menu;
