import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, elevation, colors} from '../../utilities';
import {SliderContainer} from '../Container';
import Joy from '../svg/Joy.jsx';
import {SliderLink} from '../Button';

const Maquinitas = ({className}) => {
  return (
    <SliderContainer className={className}> 
      <div className="icon"><Joy size={40}/></div>      
      <h3 className="title slide_title">Maquinitas</h3>
      <div className="text">
      <p className="txt">Xbox y Arcade (retro)</p>
      </div>
      <SliderLink>
        <Link to={'/items/maquinita'}>Explorar</Link> 
      </SliderLink>      
    </SliderContainer>

  );
}

const SlideMaquinitas = styled(Maquinitas)`
  background-image: linear-gradient(to right, ${colors.blue_2}, ${colors.blue_6} );  

  background-color: #77bcef;
background-image: url("data:image/svg+xml,%3Csvg width='80' height='80' viewBox='0 0 80 80' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23004bff' fill-opacity='0.21'%3E%3Cpath d='M50 50c0-5.523 4.477-10 10-10s10 4.477 10 10-4.477 10-10 10c0 5.523-4.477 10-10 10s-10-4.477-10-10 4.477-10 10-10zM10 10c0-5.523 4.477-10 10-10s10 4.477 10 10-4.477 10-10 10c0 5.523-4.477 10-10 10S0 25.523 0 20s4.477-10 10-10zm10 8c4.418 0 8-3.582 8-8s-3.582-8-8-8-8 3.582-8 8 3.582 8 8 8zm40 40c4.418 0 8-3.582 8-8s-3.582-8-8-8-8 3.582-8 8 3.582 8 8 8z' /%3E%3C/g%3E%3C/g%3E%3C/svg%3E");

`;

export default SlideMaquinitas;
