import React from 'react';
import styled from 'styled-components';
import { colors } from '../utilities';
import Cancel from '../elements/svg/Cancel';
import DateChange from '../elements/svg/DateChange';
import Change from '../elements/svg/Change';
import Space from '../elements/svg/Space';


const CondicionesCancelacionBase = ({className}) => {
    return (
        <div className={className}>
            <div className="cancel">
                <Cancel className="cancel_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-cancel">En caso de cancelación del evento ningún anticipo será reembolsable.</li>
                    <li className="txt-cond-cancel">Lo anterior es independientemente de la anticipación con que se realice la cancelación.</li>
                </div>
            </div>
            <div className="date_change">
                <DateChange className="cancel_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-date_change">En cambios de fecha contratada, no habrá penalización cuando se realicen con al menos 5 día de anticipación.</li>
                    <li className="txt-cond-date_change">En caso contrario se sancionará con el 50% del anticipo sin excepción.</li>
                </div>
            </div>
            <div className="game_change">
                <Change className="cancel_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-game_change">En cambios de modelo contratado, no habrá penalización cuando se realicen con al menos 3 días de anticipación.</li>
                    <li className="txt-cond-game_change">Todo cambio de juego esta sujeto a disponibilidad.</li>
                </div>
            </div>
            <div className="space">
                <Space className="cancel_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-space">Si al instalar el cliente no cuenta con espacio suficiente, será considerará como cancelación.</li>
                    <li className="txt-cond-space">Cualquier anomalía en el suministro de energía electrica será responsabilidad del cliente.</li>
                </div>
            </div>


        </div>
    );
};

const CondicionesCancelacion = styled(CondicionesCancelacionBase)`
    position: relative;
    width: 100%;
    margin-top: 8rem;


    .cancel_icon{
        display: inline-block;      
    }

    .list{
        display: inline-block;
        margin-left: 0.5rem;
        list-style-type: square;
    }


    .cancel{
        width: 95%;
        height: 5rem;
        margin: 1rem 0 2.5rem 3rem;
    }

    .date_change{
        width: 95%;
        height: 5rem; 
        margin: 0.5rem 0 2.5rem 3rem;
    }

    .game_change{
        width: 95%;
        height: 5rem;        
        margin: 0.5rem 0 2.5rem 3rem;
    }

    .space{
        width: 95%;
        height: 5rem;      
        margin: 0.5rem 0 2.5rem 3rem;
    }


    .txt-cond-cancel{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }

    .txt-cond-date_change{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }

    .txt-cond-game_change{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;        
    }

    .txt-cond-space{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }


`



export default CondicionesCancelacion;
