import React, {useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux';
//import useCustomFetch from '../../hooks/useCustomFetch';
import {detailPackage} from '../../actions/itemActions';
import {PaqDetailContainer} from '../../elements/Container';
import ItemDetailCard from '../../layout/ItemDetailCard';

function PaqDetail({match}) {

    const packageDetail = useSelector(state => state.paquete);
    const {paquete, loading } = packageDetail;
    const dispatch = useDispatch();

    useEffect( () => {
        dispatch(detailPackage(match.params.id))
    }, []);

    return (
        <PaqDetailContainer>
            {loading && <div>LOADING</div>}
            { paquete &&  <ItemDetailCard
                nombre={paquete.nombre}
                precio={paquete.precioPaquete}
                desc={paquete.descripcion}
                observaciones={paquete.observaciones}
                medidas={paquete.medidas}
            />
            }       
        </PaqDetailContainer>
    )
}

export default PaqDetail;

