import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, below, colors, absolute} from '../../utilities';
import {SliderContainer} from '../Container';
import {SliderLink} from '../Button';

const Noryan = ({className}) => {
  return (
    <SliderContainer className={className}>
      <h1 className="title">Inflables, Futbolitos, Rockolas, Maquinitas y mucho más ...!</h1>
      <div className="logo"></div>
      <SliderLink>
      <Link to={'/items/inflable'} >Explorar</Link>  
      </SliderLink>
      <p className="txt">CDMX, Estado de México & Cuernavaca</p>
    </SliderContainer>
  );
}

const SlideNoryan = styled(Noryan)`
  background-image: linear-gradient(to left, ${colors.blue_5}, ${colors.blue_1} );  
  text-align: center;

  .title{    
    padding: 2rem 0;
  }

  .logo{    
    background-image: url('https://cdn1.bbcode0.com/uploads/2020/7/13/fcae2fb3af62db254607c2671ed3176b-full.png');
    background-position: center;
    background-repeat: no-repeat;
    width: 100%;
    height: 20%;
    margin: 2rem auto;
    transform: scale(1.3);
  }

  .txt{
    padding: 2rem 0;
  }

  ${below.md`
    .title{    
      ${setFlex({y:"flex-start"})};
      font-size: 2.1rem;
      padding: 2rem 0 1rem;
    }
    .logo{      
      transform: scale(1);
    }
    .txt{
      margin: 1.5em auto 0rem;
      width: 100%;
      font-size: 1.4rem;
      text-align: center;
      margin-bottom: 2rem;
    }
  `}

  ${below.sm`    
    .title{      
      width: 90%;
      ${setFlex({y:"flex-start"})};
      font-size: 2rem;
      line-height: 2.5rem;
      margin-top: 2rem;
    }

    .logo{      
      transform: scale(.8);
    }

    .txt{
      width: 90%;
      font-size: 1.5rem;
      font-weight: 600;
      letter-spacing: .3rem;      
    }
  `}

${below.xsm`

    .title{      
      width: 90%;
      ${setFlex({y:"flex-start"})};
      font-size: 1.5rem;      
    }

    .logo{
      background-image: url('https://i.postimg.cc/tR2VhhzY/turtle-sm.png');
      width: 100%;
      transform: scale(1);
      display: block;
    }

    .txt{
      width: 90%;
      font-size: 1.2rem;
      font-weight: 600;
      letter-spacing: .3rem;
    }
  `}
`;

export default SlideNoryan;
