import React from 'react';
import styled from 'styled-components';
import {AuthContainer} from '../../elements/Container';
import {elevation, demotion, colors} from '../../utilities'

const SignupBase = ({className}) => {
  return (
    <AuthContainer>
      <div className="logo"></div>
      <form action="" className={className}>          
          <label htmlFor=""  className="label">Nombre</label>
          <input type="text" name="name" placeholder="Ingresa tu nombre"/>

          <label htmlFor="" className="label">Email</label>
          <input type="email" name="email" placeholder="Ingresa tu correo"/>

          <label htmlFor="" className="label">Contraseña</label>
          <input type="text" placeholder="* * * * * * * *"/>  

          <label htmlFor="" className="label">Confirmar contraseña</label>
          <input type="text" placeholder="* * * * * * * *"/>            
      </form>      
    </AuthContainer>
  );
}

const Signup = styled(SignupBase)`    
  width: 90%;  
  height: auto;  
  ${demotion[3]};
  border-radius: .5rem;
  display: flex;
  flex-flow: column nowrap;
  margin-top: 3rem;
  padding: 2rem 0;


  label{
    color: ${colors.white_1};    
    font-size: .8rem;
    font-weight: 500;
    margin: .7rem;
    margin-left: 1.5rem;
  }

  input{
    width: 90%;
    height: 3rem;
    border: none;
    ${demotion[3]};    

    border-radius: .5rem;
    margin: 0 auto;
  }

  input::placeholder{    
    color: ${colors.txt_2};
    opacity: .7;
    padding-left: 1rem;
  }

  ::-webkit-input-placeholder { /* Chrome/Opera/Safari */
  color: pink;
  }
  ::-moz-placeholder { /* Firefox 19+ */
    color: pink;
  }
  :-ms-input-placeholder { /* IE 10+ */
    color: pink;
  }
  :-moz-placeholder { /* Firefox 18- */
    color: pink;
  }
`;

export default Signup;
