import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex, below, absolute } from '../../utilities';
import {SliderContainer} from '../Container';
import Fut from '../svg/Fut.jsx';
import {SliderLink} from '../Button';

const Futbolito = ({className}) => {
  return (
    <SliderContainer className={className}>
      <div className="icon"><Fut size={40}/></div>
      <h3 className="title">Futbolitos</h3>      
      <div className="img_box">
        <img className="img" src="https://cdn1.bbcode0.com/uploads/2020/8/8/ba8e5bb999fdc728aa951bc67b24d190-full.png" alt=""/>
        <img className="img_grass" src="https://cdn1.bbcode0.com/uploads/2020/8/8/c97f3e263ebee91471c6f3f6c170649d-full.png" alt=""/>
        <p className="txt">Futbolitos tama√±o infantil y para adulto </p>
        <div className="link">
          <SliderLink>
            <Link to={'/items/futbolito'}>Explorar</Link>
          </SliderLink>
        </div>
      </div>            
    </SliderContainer>
  );
}

const SlideFut = styled(Futbolito)`
  background-image: linear-gradient(to right, ${colors.blue_6}, ${colors.blue_2});

  /*
    background-image: url('https://cdn1.bbcode0.com/uploads/2020/8/8/c97f3e263ebee91471c6f3f6c170649d-full.png');
  background-size: cover;
  background-position: center;
   */  

  .title{
    width: auto;
    margin: 1rem auto;
    padding-top: 1.5rem;
    padding-left: 10%;    
  }

  .img_box{
    width:80%;
    height: 100%;    
    margin: 1rem 0rem;    

    img{      
      width:100%;
      height:auto;
    }

    .txt{
      width: 50%;
      ${absolute({ x:"30%", y:"70%" })};
    }

    .link{
      ${absolute({ x:"55%", y:"80%" })};
    }      
  }


  ${below.lg`

  `}

  ${below.md`
    .title{      
      padding-left: 15%;    
    }

    .img_box{
      width:100%;
      height: 70%;    
      margin: 1rem 0rem;    

      img{      
        width:100%;
        height:auto;      
      }

      .txt{
        width: 50%;
        ${absolute({ x:"30%", y:"70%" })};
      }

      .link{
        ${absolute({ x:"55%", y:"80%" })};
      }      
    }
  `}

  ${below.sm`
    .title{      
      padding-left: 25%;
    }    
  `}


`;

export default SlideFut;

