import {ITEM_LIST_REUQEST, ITEM_LIST_SUCESS, ITEM_LIST_FAIL, ITEM_DETAIL_REQUEST, ITEM_DETAIL_SUCCESS, ITEM_DETAIL_FAIL} from '../actions/types'

function itemReducer(state={ items: []}, action){  
  switch(action.type){
    case ITEM_LIST_REUQEST:
      return { loading: true}
    case ITEM_LIST_SUCESS:
      return { loading: false, items: action.payload} 
    case ITEM_LIST_FAIL:
      return {loading: false, error: action.payload }    
    default:
      return state;  
  }
}

function itemDetailReducer(state={ items: []}, action){  
  switch(action.type){    
    case ITEM_DETAIL_REQUEST:
      return { loading: true}
    case ITEM_DETAIL_SUCCESS:
      return { loading: false, item: action.payload} 
    case ITEM_DETAIL_FAIL:
      return {loading: false, error: action.payload }  
    default:
      return state;  
  }
}

export {itemReducer, itemDetailReducer};
