import React from 'react';
import styled from 'styled-components';
import {setFlex, below} from '../../utilities';

const SlideOne = ({className}) => {
  return (
    <div className={className}>
      <div className="logo"></div>
    </div>
  );
}

const SOne = styled(SlideOne)`
  ${setFlex()};  
  width: 100%;
  height: 70vh;

  .logo{
    position: relative;
    background-image: url('https://cdn1.bbcode0.com/uploads/2020/7/13/fcae2fb3af62db254607c2671ed3176b-full.png');
    background-position: center;
    background-repeat: no-repeat;
    width: 100%;
    height: 50%;
    margin-top: .5rem;
    transform: scale(1.4);
  }

  ${below.md`
    width: 100%;
    height: 50%;
    margin-top: .5rem;
    transform: scale(.8);
  `}
`;

export default SOne;
