import React from 'react';
import styled from 'styled-components';
import {SiliderButton} from '../elements/Button';

const SliderBase = ({className}) => {

  let sliderComponents = [1,2,3,4];

  return (

    <div className={className}>
      {sliderComponents.map( (component,idx) => {
            return (
              <div key={idx} className="slide">
                {component}
              </div>
            )
      })}

      <button className="slide_btn">left</button>
      <button className="slide_btn">right</button>
    </div>
  );
}

const Slider = styled(SliderBase)`
  background-color: red;
  width: 100%;
  height: 90vh;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;

  .slide{
    background-color: blue;
    min-width: 100%;
    height: 80vh;
  }

  .slide_btn{
    background-color: yellowgreen;
  }

`;


export default Slider;
